import React, { Component } from 'react'
// import { DB_CONFIG } from '../db/Firebase.js';
import '../css/AddCard.css';
import firebase from 'firebase';

export class AddCard extends Component {
    constructor(props) {
        super(props);
        this.state = {
            eng: '',
            pin: '',
            han: '',
            done: 0
        }
        
        this.database = firebase.database().ref().child('cards');
    }

    onChange = (e) => {
        this.setState({[e.target.name]: e.target.value})
    }

    onSubmit = (e) => {
        e.preventDefault();
        this.database.push().set(this.state);
        this.setState({ 
            eng: '',
            pin: '',
            han: '',
        });
    }

    render() {
        return (
            <form onSubmit={this.onSubmit} style={{ display: 'flex' }}>
                <input style={{ flex: '10', padding: '5px' }} type="text" name="eng" placeholder="Add English ..." value={this.state.eng} onChange={this.onChange}/>
                <input style={{ flex: '10', padding: '5px' }} type="text" name="pin" placeholder="Add Pinyin ..." value={this.state.pin} onChange={this.onChange}/>
                <input style={{ flex: '10', padding: '5px' }} type="text" name="han" placeholder="Add Hanzi ..." value={this.state.han} onChange={this.onChange}/>
                <input style={{ flex: '1' }} type="submit" value="submit" className="btn" />
            </form>
        )
    }
}

export default AddCard;
