import React, { Component } from 'react'
import Card from './components/Card';
import AddCard from './components/AddCard';
import SearchCard from './components/SearchCard';
import { DB_CONFIG } from './db/Firebase.js';
import firebase from 'firebase';
import './App.css';
export class App extends Component {
  constructor(props) {
    super(props)
    this.app = firebase.initializeApp(DB_CONFIG);
    this.database = this.app.database().ref().child('cards');
    this.databaseLearned = this.app.database().ref().child('cards-learned');
    this.state = {
      cards: [],
      cardsDone: [],
      card: {},
      index: 0
    }
  }

  componentWillMount() {
    const dbCards = this.state.cards;
    const dbCardsDone = this.state.cardsDone;
    this.database.on('child_added', snap => {
      dbCards.push({
        id: snap.key,
        eng: snap.val().eng,
        han: snap.val().han,
        pin: snap.val().pin,
        done: snap.val().done,
        date: snap.val().date
      })
      this.setState({
        cards: this.state.cards,
        card: this.state.cards[0],
        index: 0
      })
    })

    this.databaseLearned.on('child_added', snap => {
      dbCardsDone.push({
        childId: snap.key,
        parentId: snap.val().id,
        eng: snap.val().eng,
        han: snap.val().han,
        pin: snap.val().pin,
        done: snap.val().done,
        date: snap.val().date
      })
      this.setState({
        cardsDone: dbCardsDone
      })
    })
    // this.cardTimeDone;

    console.log(this.state.cardsDone);

    this.state.cardsDone.map(card => {
      if(Date.parse(card.date) > 100000000000) {
        this.databaseLearned.child(card.childId).remove();
      }
    })

  }

  // componentDidMount() {
  //   // console.log(this.resetCardTime(this.state.cardsDone, this.state.cards));
  //   this.state.cards.map(card => {
  //     return (
  //       this.state.cardsDone.forEach(cardDone => {
  //         if(cardDone.id === card.id) {
  //           this.databaseLearned.child(cardDone.childId).remove();
  //         }
  //       })
  //     )
  //   });
  // }
  // cardTimeDone = () => {
  //   let cardAdded = new Date();
  // }

  resetCardTime = ([...cardsDone], [...cards]) => {
    cardsDone.map((cardDone) => {
      return cards.includes(cardsDone.id);
    });
  }
  updateNextCard = (cards) => {
    var i = this.state.index;
    if(!(i+1 in cards)) {
      return cards[0];
    } else {
      return cards[i+1];
    }
    // const card = cards[Math.floor(Math.random() * cards.length)];
    // return card;
  }
  appendIndex = (index) => {
    var list = this.state.cards;
    if(index+1 > list.length - 1) {
      return 0;
    } else {
      return index + 1;
    }
  }
  nextCard = () => {
    this.setState({
      cards: this.state.cards,
      card: this.updateNextCard(this.state.cards),
      index: this.appendIndex(this.state.index)
    })
    var cardsDonezo = this.state.cardsDone;

    cardsDonezo.map((card) => {
      return console.log(Date.parse(new Date()));
    })
  }
  updatePrevCard = (cards) => {
    var i = this.state.index;
    if(!(i-1 in cards)) {
      return cards[cards.length - 1];
    } else {
      return cards[i-1];
    }
    // const card = cards[Math.floor(Math.random() * cards.length)];
    // return card;
  }

  subtractIndex = (index) => {
    var list = this.state.cards;
    if(index-1 < 0) {
      return list.length - 1;
    } else {
      return index - 1;
    }
  }
  prevCard = () => {
    this.setState({
      cards: this.state.cards,
      card: this.updatePrevCard(this.state.cards),
      index: this.subtractIndex(this.state.index)
    })
  }
  resetState = () => {
    this.setState({
      cards: [],
      card: {},
      index: 1
    })
  }
  removeCard = (id) => {
    console.log(id);
    this.resetState();
    var refList = [];
    console.log(refList);
    this.database.child(id).remove();
    var newCards = this.refreshCards(refList);
    this.setState({
      cards: newCards,
      card: newCards[0],
      index: 1
    })
  }
  refreshCards = (cardList) => {
    this.database.on('child_added', snap => {
      cardList.push({
        id: snap.key,
        eng: snap.val().eng,
        han: snap.val().han,
        pin: snap.val().pin,
        done: snap.val().done,
        date: snap.val().date
      })
    })
    return cardList;
  }
  cardLearned = (card) => {
    var day = new Date();
    console.log(day); // Apr 30 2000

    var nextDay = new Date(day);
    const tomorrow = nextDay.setDate(day.getDate()+1);
    // console.log(nextDay);
    // var date = tomorrow.getFullYear()+'-'+(tomorrow.getMonth()+1)+'-'+tomorrow.getDate();
    console.log(card.done, tomorrow);
    this.databaseLearned.push().set({
      parentId: card.id,
      eng: card.eng,
      pin: card.pin,
      han: card.han,
      done: card.done + 1,
      date: tomorrow
    });
  }
  render() {
    return (
      <div className="App">
        <h1>Flashcard app</h1>
        <Card card={this.state.card} prevCard={this.prevCard} nextCard={this.nextCard} removeCard={this.removeCard} cardLearned={this.cardLearned}/>
        <AddCard />
        <SearchCard cards={this.state.cards}/>
      </div>
    )
  }
}

export default App;
